import React, { useState } from "react";

function App() {
  const purple = "#FF00FF";
  const [bg, setBg] = useState(purple);

  const text = "Click Me";
  const [txt, setTxt] = useState(text);

  const bgChange = () => {
    let newBg = "#FFFF00";
    setBg(newBg);
  };

  const txtChange = () => {
    let newTxt = "Dude!!!";
    setTxt(newTxt);
  }

  return (
    <>
      <div style={{ backgroundColor: bg }}>
        <button onClick={bgChange}>Click Me</button>
      </div>
    </>
  );
}

export default App;
