            placeholder="Your first name here"
            onChange={inputEvent}
            value={fullname.fname}
          />
          <br />
          <input
            type="text"
            placeholder="Your last name here"
            onChange={inputEvent}
            value={fullname.lname}
          />
          <button onClick={onSubmit}> Click Here</button>
        </form>
      </div>
    </>
  );
};

export default App;
