import React, { useState } from "react";

const App = () => {

  const [name, setName] = useState('my guy')
  const [fullname, setFullname] = useState('my guy')
  const inputEvent = (event) => {
    setName(event.target.value)
  }

  const onSubmit = () => {
    setFullname(name)
  }

  return (
    <>
      <div>
        <h1>Hello {fullname}!</h1>
        <input type="text" placeholder="Your name here" onChange={inputEvent} value={name}/>
        <button onClick={onSubmit}> Click Here</button>
      </div>
    </>
  );
};

export default App;
