import React from "react";

function App() {

    const bgChange = () => {
        purple = "#FF00FF"
        const [bg, setBg] = useState(purple)
    }

    return (
    <>
      <div>
        <button onClick={bgChange}>Click Me</button>
      </div>
    </>
  );
}

export default App;
