import React, { useState } from "react";

function App() {
  const purple = "#FF00FF";
  const [bg, setBg] = useState(purple);

  const text = "Click Me";
  const [txt, setTxt] = useState(text);

  const bgChange = () => {
    let newBg = "#FFFFFF";
    if (Bg == "#FF00FF") {
      newBg = "#00FFFF";
    } else if (Bg == "#00FFFF") {
      newBg = "#FFFF00";
    } else {
      newBg = "#FF00FF";
    }

    setBg(newBg);

    let newTxt = "Sup!";
    if (txt == "Click Me") {
      newTxt = "Dude!!!";
    } else if (txt == "Dude!!!") {
      newTxt = "HEYYY!";
    } else {
      newTxt = "Click Me";
    }

    setTxt(newTxt);
    
  };

  const changeBack = () => {
    let newBg = "#FF00FF";
    setBg(newBg);
    let newTxt = "Okay dude!";
    setTxt(newTxt);
  };

  return (
    <>
      <div style={{ backgroundColor: bg }}>
        <button onClick={bgChange} onDoubleClick={changeBack}>
          {txt}
        </button>
      </div>
    </>
  );
}

export default App;
